  const sortedSkills = skills.sort((a, b) => (a.order > b.order ? 1 : -1));
  const firstColor = sortedSkills[0].color;
  const material = useSignal<PointsMaterial>();

  useVisibleTask$(({ cleanup }) => {
    const scene = new Scene();
    scene.fog = new FogExp2(0x000000, 0.001);

    const camera = new PerspectiveCamera(
      55,
      window.innerWidth / window.innerHeight,
      2,
      2000,
    );
    camera.position.z = 1000;

    const renderer = new WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    const ambientLight = new AmbientLight('white');
    scene.add(ambientLight);

    let mouseX = 0,
      mouseY = 0;

    let windowHalfX = window.innerWidth / 2;
    let windowHalfY = window.innerHeight / 2;

    const geometry = new BufferGeometry();
    const vertices = [];

    const sprite = new TextureLoader().load('/assets/disc.png');

    for (let i = 0; i < 10000; i++) {
      const x = 2000 * Math.random() - 1000;
      const y = 2000 * Math.random() - 1000;
      const z = 2000 * Math.random() - 1000;

      vertices.push(x, y, z);
    }

    geometry.setAttribute('position', new Float32BufferAttribute(vertices, 3));

    material.value = noSerialize(
      new PointsMaterial({
        size: 35,
        sizeAttenuation: true,
        map: sprite,
        alphaTest: 0.5,
        transparent: true,
        color: new Color(firstColor),
      }),
    );

    const particles = new Points(geometry, material.value);
    scene.add(particles);

    document.body.style.touchAction = 'none';
    document.body.addEventListener('pointermove', onPointerMove);

    window.addEventListener('resize', onWindowResize);

    function onWindowResize() {
      windowHalfX = window.innerWidth / 2;
      windowHalfY = window.innerHeight / 2;

      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();

      renderer.setSize(window.innerWidth, window.innerHeight);
    }

    function onPointerMove(event: PointerEvent) {
      if (event.isPrimary === false) return;

      mouseX = event.clientX - windowHalfX;
      mouseY = event.clientY - windowHalfY;
    }

    function render() {
      camera.position.x += (mouseX - camera.position.x) * 0.05;
      camera.position.y += (-mouseY - camera.position.y) * 0.05;

      camera.lookAt(scene.position);

      renderer.render(scene, camera);
    }

    function animate() {
      requestAnimationFrame(animate);

      render();
    }
    animate();

    cleanup(() => {
      window.removeEventListener('resize', () => {});
      renderer.domElement.remove();
      renderer.renderLists.dispose();
      renderer.dispose();
    });
  });

  return (
    <>
      <div class="my-40 w-full">
        <div class="container mx-auto">
          <div class="grid grid-cols-1 gap-x-60 gap-y-8 md:grid-cols-2 lg:grid-cols-3">
            {sortedSkills.map(({ name, component, color }, index) => (
              <div
                key={index}
                class="w-full content-center rounded bg-white p-10"
                onMouseEnter$={() => {
                  if (material.value) {
                    material.value.color = new Color(color);
                  }
                }}
              >
                <div class="flex justify-center text-8xl">{component}</div>

                <div class="pt-5">
                  <h5 class="mb-0 text-center text-2xl font-bold tracking-tight text-black">
                    {name}
                  </h5>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
